mapper.select:
SELECT




FROM user


        AND id = #{id}


        AND username = #{username}

mapper.update:
UPDATE user
SET update_date = CURRENT_TIMESTAMP

    , username = #{username}


    , password = #{password}


    , name = #{name}


    , email = #{email}


    , address = #{address}


    , mobile = #{mobile}


    , role = #{role}

WHERE id = #{id}
